    public PyTripWorker(int obergrenze)
    {
      this.obergrenze = obergrenze;
    }

    @Override
    protected Calc doInBackground() throws Exception
    {
     return new Calc(obergrenze);
    }

    @Override
    protected void done()
    {
      
    }
    
