  private void onX()
  {
    if (JOptionPane.showConfirmDialog(this, "Wirklich beenden?", 
      "Sicherheitsabfrage", JOptionPane.YES_NO_OPTION) == JOptionPane.YES_OPTION)
      dispose();
  }
